class ReferalEarningsScreen extends StatefulWidget {
  const ReferalEarningsScreen({super.key});

  @override
  State<ReferalEarningsScreen> createState() => _ReferalEarningsScreenState();
}

class _ReferalEarningsScreenState extends State<ReferalEarningsScreen> {
  @override
  void initState() {
    // TODO: implement initState
    context.read<ReferralListCubit>().fetchReferralList();
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: const Color(0xFF223DFE),
      appBar: AppBar(
        automaticallyImplyLeading: false,
        backgroundColor: const Color(0xFF223DFE),
        leading: IconButton(
          icon: Container(
            padding: const EdgeInsets.all(6),
            decoration: BoxDecoration(
              color: Colors.grey[200],
              borderRadius: BorderRadius.circular(6),
            ),
            child: const Icon(
              Icons.arrow_back_ios_new_rounded,
              color: Colors.black,
              size: 16,
            ),
          ),
          onPressed: () {
            Navigator.pop(context);
          },
        ),
        elevation: 0,
      ),
      body: Column(
        mainAxisAlignment: MainAxisAlignment.start,
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Container(
            decoration: BoxDecoration(
              color: AppthemeColor().appMainColor,
            ),
            width: double.infinity,
            child: Column(
              mainAxisAlignment: MainAxisAlignment.center,
              crossAxisAlignment: CrossAxisAlignment.center,
              children: [
                const CommonProximaNovaTextWidget(
                    text: 'Refer your friends',
                    fontSize: 22,
                    fontWeight: FontWeight.w600,
                    color: Colors.white),
                const CommonProximaNovaTextWidget(
                    text: 'and Earn',
                    fontSize: 24,
                    fontWeight: FontWeight.w600,
                    color: Colors.white),
                const SizedBox(
                  height: 10,
                ),
                BlocBuilder<ReferralListCubit, ReferralListState>(
                  builder: (context, state) {
                    if (state.listStatus == CurrentRequestStatus.loading) {
                      return const Padding(
                        padding: EdgeInsets.symmetric(horizontal: 40),
                        child: Text.rich(
                          TextSpan(
                            children: [
                              TextSpan(
                                text: 'Refer a Friend .You will get ',
                                style: TextStyle(
                                  color: Color(0xFFE7E7E7),
                                  fontSize: 13,
                                  fontFamily: 'ProximaNova',
                                  fontWeight: FontWeight.w400,
                                  height: 1.31,
                                ),
                              ),
                              TextSpan(
                                text: '',
                                style: TextStyle(
                                  color: Color(0xFFE7E7E7),
                                  fontSize: 16,
                                  fontFamily: 'ProximaNova',
                                  fontWeight: FontWeight.w600,
                                  height: 1.06,
                                ),
                              ),
                              TextSpan(
                                text:
                                    ' on his/her First Purchase Use the Referal',
                                style: TextStyle(
                                  color: Color(0xFFE7E7E7),
                                  fontSize: 13,
                                  fontFamily: 'ProximaNova',
                                  fontWeight: FontWeight.w400,
                                  height: 1.31,
                                ),
                              ),
                            ],
                          ),
                          textAlign: TextAlign.center,
                        ),
                      );
                    }
                    ReferListModel? referralData = state.referList;
                    return Padding(
                      padding: const EdgeInsets.symmetric(horizontal: 40),
                      child: Text.rich(
                        TextSpan(
                          children: [
                            const TextSpan(
                              text: 'Refer a Friend .You will get ',
                              style: TextStyle(
                                color: Color(0xFFE7E7E7),
                                fontSize: 13,
                                fontFamily: 'ProximaNova',
                                fontWeight: FontWeight.w400,
                                height: 1.31,
                              ),
                            ),
                            TextSpan(
                              text: 'Rs.${referralData?.referedAmount ?? ""}',
                              style: const TextStyle(
                                color: Color(0xFFE7E7E7),
                                fontSize: 16,
                                fontFamily: 'ProximaNova',
                                fontWeight: FontWeight.w600,
