                            )),
                        onPressed: () {
                          Navigator.push(
                              context,
                              CupertinoPageRoute(
                                builder: (context) => ReferAFriendScreen(
                                  amount: referralData?.referedAmount ?? '',
                                ),
                              ));
                          // shareimage(
                          //   referralData
                          //       .data.referralCode,
                          //   referralData.data.description,
                          //   referralData.data.image,
                          // );
                        },
